// Assuming "times" and "values" are arrays of floating-point numbers\n// with the same length, and "n" is the length of the arrays\n\nfor (i = 0; i < n-1; i++) {\n  for (j = i+1; j < n; j++) {\n    if (times[i] > times[j]) {\n      // Swap times[i] and times[j]\n      tempTime = times[i]\n      times[i] = times[j]\n      times[j] = tempTime\n\n      // Swap values[i] and values[j]\n      tempValue = values[i]\n      values[i] = values[j]\n      values[j] = tempValue\n    }\n  }\n}