// Allocate the array of pointers\nint* *multi = malloc(10 * sizeof(int*));\n\n// Initialize each pointer to NULL\nfor (int i = 0; i < 10; i++) {\n    multi[i] = NULL;\n}\n\n// Allocate memory for a specific index\nmulti[5] = malloc(20 * sizeof(int));\n\n// Assign a value\nmulti[5][11] = 66;\n\n// Free memory\nfor (int i = 0; i < 10; i++) {\n    if (multi[i] != NULL) {\n        free(multi[i]);\n        multi[i] = NULL; // Set to NULL to prevent dangling pointers\n    }\n}\n\nfree(multi);