#import <sqlite3.h>\n\n// ...\n\nsqlite3 *db;\nsqlite3_stmt *stmt;\n\n// Open the database\nif (sqlite3_open("your_database.db", &db) != SQLITE_OK) {\n    // Handle error\n}\n\n// Prepare the statement\nconst char *sql = "SELECT * FROM your_table WHERE name = ?";\nif (sqlite3_prepare_v2(db, sql, -1, &stmt, NULL) != SQLITE_OK) {\n    // Handle error\n}\n\n// Bind the parameter\nNSString *name = @"John";\nif (sqlite3_bind_text(stmt, 1, [name UTF8String], -1, SQLITE_STATIC) != SQLITE_OK) {\n    // Handle error\n}\n\n// Execute the statement\nwhile (sqlite3_step(stmt) == SQLITE_ROW) {\n    // Fetch data\n}\n\n// Finalize the statement\nsqlite3_finalize(stmt);\n\n// Close the database\nsqlite3_close(db);