// Example of a simple B-tree node\nstruct btree_node {\n    int keys[4];\n    int child_pointers[5];\n};\n\nvoid search_btree(int key, struct btree_node* root) {\n    // Start at the root node\n    struct btree_node* current_node = root;\n    while (1) {\n        // Search for the key in the current node\n        int i;\n        for (i = 0; i < 4; i++) {\n            if (current_node->keys[i] >= key) {\n                break;\n            }\n        }\n        // If the key is found, return\n        if (i < 4 && current_node->keys[i] == key) {\n            return;\n        }\n        // Otherwise, descend to the child node\n        current_node = (struct btree_node*) current_node->child_pointers[i];\n    }\n}