// Example of a simple external merge sort\nvoid external_merge_sort() {\n    // Split the input file into smaller chunks\n    FILE* input_file = fopen("input.txt", "r");\n    FILE* chunk_files[10];\n    int chunk_sizes[10];\n    for (int i = 0; i < 10; i++) {\n        chunk_files[i] = fopen("chunk.txt", "w");\n        chunk_sizes[i] = 0;\n        char buffer[1024];\n        while (fread(buffer, 1, 1024, input_file) > 0) {\n            fwrite(buffer, 1, 1024, chunk_files[i]);\n            chunk_sizes[i] += 1024;\n        }\n        fclose(chunk_files[i]);\n    }\n    fclose(input_file);\n    // Merge the sorted chunks\n    FILE* output_file = fopen("output.txt", "w");\n    for (int i = 0; i < 10; i++) {\n        chunk_files[i] = fopen("chunk.txt", "r");\n    }\n    while (1) {\n        int min_value = INT_MAX;\n        int min_chunk = -1;\n        for (int i = 0; i < 10; i++) {\n            int value;\n            if (fscanf(chunk_files[i], "%d", &value) == 1) {\n                if (value < min_value) {\n                    min_value = value;\n                    min_chunk = i;\n                }\n            }\n        }\n        if (min_chunk == -1) {\n            break;\n        }\n        fprintf(output_file, "%d\n", min_value);\n        chunk_sizes[min_chunk]--;\n        if (chunk_sizes[min_chunk] == 0) {\n            fclose(chunk_files[min_chunk]);\n        }\n    }\n    fclose(output_file);\n}