// Example code snippet for using the Windows CE Remote Call Profiler\n#include <windows.h>\n#include <profile.h>\n\nint main()\n{\n    // Initialize the profiler\n    PROFILE_INIT();\n\n    // Start profiling\n    PROFILE_START();\n\n    // Code to be profiled\n    for (int i = 0; i < 10000; i++)\n    {\n        // Simulate some work\n        Sleep(1);\n    }\n\n    // Stop profiling\n    PROFILE_STOP();\n\n    // Uninitialize the profiler\n    PROFILE_UNINIT();\n    return 0;\n}