// Example C program to demonstrate DBMS-agnostic coding practices\n\n#include <stdio.h>\n#include <stdlib.h>\n\n// Define a DBMS-agnostic interface for SQL operations\ntypedef struct {\n    void (*executeQuery)(const char* query);\n    void (*closeConnection)();\n} DatabaseInterface;\n\n// Implement DBMS-specific functions for each database system\nvoid informixExecuteQuery(const char* query) {\n    // Informix-specific implementation\n    printf("Informix: Executing query '%s'\n", query);\n}\n\nvoid oracleExecuteQuery(const char* query) {\n    // Oracle-specific implementation\n    printf("Oracle: Executing query '%s'\n", query);\n}\n\n// Create a DBMS-agnostic database interface\nDatabaseInterface* createDatabaseInterface(const char* dbms) {\n    DatabaseInterface* interface = malloc(sizeof(DatabaseInterface));\n\n    if (strcmp(dbms, "informix") == 0) {\n        interface->executeQuery = informixExecuteQuery;\n    } else if (strcmp(dbms, "oracle") == 0) {\n        interface->executeQuery = oracleExecuteQuery;\n    } else {\n        // Handle unsupported DBMS\n    }\n\n    return interface;\n}\n\nint main() {\n    // Create a DBMS-agnostic database interface\n    DatabaseInterface* interface = createDatabaseInterface("informix");\n\n    // Execute a query using the DBMS-agnostic interface\n    interface->executeQuery("SELECT * FROM example_table");\n\n    return 0;\n}