// objc-parser.c\n// ...\n\n// Define the new keyword\nDEFTREECODE (OBJC_ASSERT_EXPR, "objc_assert", tcc_expression, 0)\n\n// ...\n\n// Parse the new keyword\nstatic tree\nobjc_parse_assert (void)\n{\n  tree expr = objc_parse_expression ();\n  tree string = objc_parse_string_literal ();\n  tree file = objc_get_file_name ();\n  tree line = objc_get_line_number ();\n  tree assert_expr = build (OBJC_ASSERT_EXPR, expr, string, file, line);\n  return assert_expr;\n}\n\n// ...\n\n// Define the semantics of the new keyword\nstatic tree\nobjc_handle_assert (tree expr)\n{\n  tree function = build_function_call (objc_runtime_assert, 4, expr, TREE_OPERAND (expr, 1), TREE_OPERAND (expr, 2), TREE_OPERAND (expr, 3));\n  return function;\n}\n\n// ...