// Example code to parse TCP packets\n#include <stdio.h>\n#include <stdint.h>\n\nstruct tcp_packet {\n    uint16_t src_port;\n    uint16_t dst_port;\n    uint32_t seq_num;\n    uint32_t ack_num;\n    uint8_t  flags; // SYN, ACK, RST, etc.\n    // ...\n};\n\nvoid parse_tcp_packet(const uint8_t* packet, size_t len) {\n    struct tcp_packet* tcp = (struct tcp_packet*)packet;\n    printf("SRC Port: %u\n", ntohs(tcp->src_port));\n    printf("DST Port: %u\n", ntohs(tcp->dst_port));\n    printf("SEQ Num: %u\n", ntohl(tcp->seq_num));\n    printf("ACK Num: %u\n", ntohl(tcp->ack_num));\n    printf("Flags: %02x\n", tcp->flags);\n    // ...\n}