// Browser process\nint socket_fd = socket(AF_UNIX, SOCK_STREAM, 0);\nbind(socket_fd, "/tmp/chrome-ipc-socket", sizeof(struct sockaddr_un));\nlisten(socket_fd, 1);\n\n// Renderer process\nint client_fd = socket(AF_UNIX, SOCK_STREAM, 0);\nconnect(client_fd, "/tmp/chrome-ipc-socket", sizeof(struct sockaddr_un));\n\n// Send a message from the Browser process to the Renderer process\nchar message[] = "Render this webpage";\nsend(socket_fd, message, strlen(message), 0);\n\n// Receive the message in the Renderer process\nchar buffer[1024];\nrecv(client_fd, buffer, 1024, 0);\nrender_webpage(buffer);