// Assume we have a buffer with the payload data\nchar* payload = "Hello, Server!";\nsize_t payload_len = strlen(payload);\n\n// Create a packet buffer\npacket_t* packet = malloc(sizeof(packet_t) + payload_len);\npacket->length = htons(payload_len);  // Convert to network byte order\npacket->type = 1;  // Login packet, for example\nmemcpy(packet->payload, payload, payload_len);\n\n// Send the packet over the network\nsend(sockfd, packet, sizeof(packet_t) + payload_len, 0);