// Example of a 4-way processor executing 4 instructions simultaneously\nvoid four_way_example() {\n    int a = 2 + 2;  // Instruction 1\n    int b = 3 * 3;  // Instruction 2\n    int c = 4 - 1;  // Instruction 3\n    int d = 5 / 2;  // Instruction 4\n}\n\n// Example of an 8-way processor executing 8 instructions simultaneously\nvoid eight_way_example() {\n    int a = 2 + 2;  // Instruction 1\n    int b = 3 * 3;  // Instruction 2\n    int c = 4 - 1;  // Instruction 3\n    int d = 5 / 2;  // Instruction 4\n    int e = 6 + 1;  // Instruction 5\n    int f = 7 * 2;  // Instruction 6\n    int g = 8 - 3;  // Instruction 7\n    int h = 9 / 3;  // Instruction 8\n}