// Simplified representation of the process structure\nstruct proc {\n    // Process-specific fields\n    pid_t pid;\n    // ...\n};\n\n// Simplified representation of the user structure\nstruct user {\n    // User-specific fields\n    uid_t uid;\n    gid_t gid;\n    // ...\n};\n\n// When a process calls fork()\nstruct proc *parent_proc = ...;\nstruct proc *child_proc = fork(parent_proc);\n\n// The child process shares the same user structure as the parent\nchild_proc->user = parent_proc->user;