// Example of using MuPDF to render a PDF in C\n#include <mupdf/fitz.h>\n\nint main() {\n  // Load the PDF\n  fz_context *ctx = fz_new_context(NULL, NULL, FZ_STORE_DEFAULT);\n  fz_document *doc = fz_open_document(ctx, "path/to/pdf.pdf");\n\n  // Render the PDF\n  fz_page *page = fz_load_page(ctx, doc, 0);\n  fz_pixmap *pix = fz_render_page(ctx, page, fz_device_rgb(ctx), fz_identity(ctx));\n  // Display the rendered pixmap\n  // ...\n\n  return 0;\n}