// Class definition\nClassDefNode *classDef = malloc(sizeof(ClassDefNode));\nclassDef->name = "MyClass";\nclassDef->parent = NULL; // no parent\nclassDef->members = newList();\n\n// Add member variable\nMemberVarNode *var = malloc(sizeof(MemberVarNode));\nvar->name = "myVar";\nvar->type = INT_TYPE;\nvar->initValue = NULL;\naddToList(classDef->members, var);\n\n// Add member method\nMemberMethodNode *method = malloc(sizeof(MemberMethodNode));\nmethod->name = "myMethod";\nmethod->returnType = VOID_TYPE;\nmethod->params = newList();\nmethod->body = // some block node\naddToList(classDef->members, method);\n\n// Execute class definition\nexecuteClassDef(classDef);\n\n// Create instance of MyClass\nObject *obj = createObject(classDef);\n\n// Access member variable\nprintf("%d\n", getMemberVar(obj, "myVar"));\n\n// Invoke member method\ninvokeMemberMethod(obj, "myMethod");