// libspe example\n#include <libspe/spe.h>\n\nint main() {\n    spe_context_ptr_t spe_ctx;\n    spe_thread_t spe_thread;\n\n    // Create an SPE context\n    spe_ctx = spe_context_create();\n\n    // Create an SPE thread\n    spe_thread = spe_thread_create(spe_ctx, my_spe_function, NULL);\n\n    // Start the SPE thread\n    spe_thread_start(spe_thread);\n\n    // ...\n}\n\n// libspe2 example\n#include <libspe2/spe.h>\n#include <pthread.h>\n\nint main() {\n    spe_context_ptr_t spe_ctx;\n    pthread_t spe_thread;\n\n    // Create an SPE context\n    spe_ctx = spe_context_create();\n\n    // Create a pthread\n    pthread_create(&spe_thread, NULL, my_spe_function, spe_ctx);\n\n    // Start the pthread\n    pthread_start(spe_thread);\n\n    // ...\n}