// stop_watch.c\npthread_t start_stopwatch(struct data_struct *g_data_struct) {\n    pthread_t thread_id;\n    int rc;\n\n    // ...\n\n    rc = pthread_create(&thread_id, NULL, g_start_timer, (void *) g_data_struct);\n\n    if (rc) {\n        printf("=== Failed to create thread\n");\n        return 0;\n    }\n\n    return thread_id;\n}\n\n// main.c\nint main() {\n    // ...\n\n    pthread_t thread_id = start_stopwatch(g_data_struct);\n\n    // ...\n\n    pthread_join(thread_id, NULL);\n    free(g_data_struct);\n\n    return 0;\n}