// config.h\n#define NUMBER_OF_POTS  2\n#define POT_1_CHANNEL  27\n#define POT_2_CHANNEL  29\n\n// adc.c\n#define MAKE_CSS(channel) _CSS##channel\n#define CALL_MAKE_CSS(channel) MAKE_CSS(channel)\n\n#define INIT_ADC(chan) CALL_MAKE_CSS(chan)\n#define INIT_ADC_LOOP(num) INIT_ADC(POT_##num##_CHANNEL)\n\n#define INIT_ADC_ALL() \\n    _Pragma("push_macro(\"INIT_ADC\")") \\n    _Pragma("push_macro(\"INIT_ADC_LOOP\")") \\n    do { \\n        INIT_ADC_LOOP(1); \\n        INIT_ADC_LOOP(2); \\n        /* ... */ \\n    } while (0) \\n    _Pragma("pop_macro(\"INIT_ADC\")") \\n    _Pragma("pop_macro(\"INIT_ADC_LOOP\")")\n\nvoid initialize_adc() {\n    INIT_ADC_ALL();\n}