// main.c (program source code)\n#include <windows.h>\n\nint main() {\n    HMODULE hDll = LoadLibraryA("mydll.dll");\n    if (hDll != NULL) {\n        int (*addFunc)(int, int) = (int (*)(int, int))GetProcAddress(hDll, "add");\n        if (addFunc != NULL) {\n            int result = addFunc(2, 3);\n            printf("Result: %d\n", result);\n        }\n        FreeLibrary(hDll);\n    }\n    return 0;\n}