// Create a named pipe\nHANDLE hPipe = CreateNamedPipe(\n    TEXT("\\\\.\\pipe\\MyPipe"),\n    PIPE_ACCESS_DUPLEX,\n    PIPE_TYPE_BYTE | PIPE_READMODE_BYTE | PIPE_WAIT,\n    1,\n    1024,\n    1024,\n    NMPWAIT_USE_DEFAULT_WAIT,\n    NULL\n);\n\n// Connect to the named pipe from another process\nHANDLE hPipe = CreateFile(\n    TEXT("\\\\.\\pipe\\MyPipe"),\n    GENERIC_READ | GENERIC_WRITE,\n    0,\n    NULL,\n    OPEN_EXISTING,\n    0,\n    NULL\n);