// Simplified PCB/PTE structure\nstruct pcb {\n    int pid;        // Process ID\n    void *pc;       // Program Counter\n    void *sp;       // Stack Pointer\n    void *memory;   // Memory allocation information\n};\n\npcb_t my_pcb = {\n    .pid = 1234,\n    .pc = 0x1000,  // Entry point of the program\n    .sp = 0x2000,  // Initial stack pointer\n    .memory = 0x3000  // Memory allocation information\n};