// Example code in C\n#include <upek/upek.h>\n\nint main() {\n    // Initialize the library\n    upek_init();\n\n    // Open the fingerprint reader device\n    upek_device *dev = upek_open_device("/dev/bus/usb/001/002");\n\n    // Capture a fingerprint image\n    upek_image *img = upek_capture(dev);\n\n    // Process the image\n    upek_feature_set *features = upek_extract_features(img);\n\n    // Do something with the fingerprint features\n    // ...\n\n    // Clean up\n    upek_close_device(dev);\n    upek_exit();\n    return 0;\n}