// Sender\n#include <stdio.h>\n#include <stdlib.h>\n#include <string.h>\n#include <sys/socket.h>\n#include <netinet/in.h>\n#include <arpa/inet.h>\n\nint main() {\n    int sockfd;\n    struct sockaddr_in addr;\n    char buffer[1024];\n\n    sockfd = socket(AF_INET, SOCK_DGRAM, 0);\n    if (sockfd < 0) {\n        perror("socket creation failed");\n        exit(1);\n    }\n\n    addr.sin_family = AF_INET;\n    addr.sin_port = htons(12345);\n    inet_pton(AF_INET, "224.0.0.1", &addr.sin_addr);\n\n    sendto(sockfd, buffer, strlen(buffer), 0, (struct sockaddr *)&addr, sizeof(addr));\n    close(sockfd);\n    return 0;\n}\n\n// Receiver\n#include <stdio.h>\n#include <stdlib.h>\n#include <string.h>\n#include <sys/socket.h>\n#include <netinet/in.h>\n#include <arpa/inet.h>\n\nint main() {\n    int sockfd;\n    struct sockaddr_in addr;\n    char buffer[1024];\n\n    sockfd = socket(AF_INET, SOCK_DGRAM, 0);\n    if (sockfd < 0) {\n        perror("socket creation failed");\n        exit(1);\n    }\n\n    addr.sin_family = AF_INET;\n    addr.sin_port = htons(12345);\n    inet_pton(AF_INET, "224.0.0.1", &addr.sin_addr);\n\n    bind(sockfd, (struct sockaddr *)&addr, sizeof(addr));\n\n    recvfrom(sockfd, buffer, 1024, 0, NULL, NULL);\n    printf("%s\n", buffer);\n    close(sockfd);\n    return 0;\n}