// Define the version components\n#define MAJOR_VERSION 1\n#define MINOR_VERSION 0\n#define PATCH_VERSION 0\n#define BUILD_NUMBER 1\n\n// Create the version string\n#define VERSION_STRING \\n    STRINGIFY(MAJOR_VERSION) "." \\n    STRINGIFY(MINOR_VERSION) "." \\n    STRINGIFY(PATCH_VERSION) "." \\n    STRINGIFY(BUILD_NUMBER)\n\n// Use the version string\nprintf("Version: %s\n", VERSION_STRING);