// Example code in C\n#include <nbis/nbis.h>\n\nint main() {\n    // Initialize the library\n    nbis_init();\n\n    // Open the fingerprint reader device\n    nbis_device *dev = nbis_open_device("/dev/bus/usb/001/002");\n\n    // Capture a fingerprint image\n    nbis_image *img = nbis_capture(dev);\n\n    // Process the image\n    nbis_feature_set *features = nbis_extract_features(img);\n\n    // Do something with the fingerprint features\n    // ...\n\n    // Clean up\n    nbis_close_device(dev);\n    nbis_exit();\n    return 0;\n}