// Example client code\n#include <stdio.h>\n#include <stdlib.h>\n#include <string.h>\n#include <openssl/ssl.h>\n#include <openssl/err.h>\n\n#define CHK_SSL(err) if ((err) < 1) { ERR_print_errors_fp(stderr); exit(2); }\n\nint main() {\n    SSL_CTX* ctx;\n    SSL* ssl;\n    BIO* bio;\n\n    // Initialize OpenSSL\n    SSL_library_init();\n    SSL_load_error_strings();\n\n    // Create an SSL context\n    ctx = SSL_CTX_new(TLS_client_method());\n    CHK_SSL(SSL_CTX_set_default_verify_paths(ctx));\n\n    // Create a BIO and connect to the server\n    bio = BIO_new_connect("www.example.com:443");\n    CHK_SSL(bio);\n\n    // Create an SSL object\n    ssl = SSL_new(ctx);\n    CHK_SSL(SSL_set_bio(ssl, bio, bio));\n\n    // Connect and perform the handshake\n    CHK_SSL(SSL_connect(ssl));\n\n    // Verify the server's certificate\n    CHK_SSL(SSL_get_verify_result(ssl));\n\n    // Send data\n    CHK_SSL(SSL_write(ssl, "Hello, server!", 13));\n\n    // Shutdown the connection\n    SSL_shutdown(ssl);\n\n    // Clean up\n    SSL_free(ssl);\n    BIO_free(bio);\n    SSL_CTX_free(ctx);\n\n    return 0;\n}