// Compile with: gcc -pg -pthread your_program.c -o your_program\n\n#include <pthread.h>\n\n// ... your code ...\n\nint main() {\n    // ... your code ...\n\n    // Create and start threads\n    pthread_t thread1, thread2;\n    pthread_create(&thread1, NULL, your_thread_func, NULL);\n    pthread_create(&thread2, NULL, your_thread_func, NULL);\n\n    // Wait for threads to finish\n    pthread_join(thread1, NULL);\n    pthread_join(thread2, NULL);\n\n    return 0;\n}