// Assume you have a 2D array of pixel values (e.g., 0 = background, 1 = foreground)\n\nvoid floodFill(int x, int y, int color, int** pixels, int width, int height) {\n  // Check if pixel is within bounds and has the same color\n  if (x < 0 || x >= width || y < 0 || y >= height || pixels[y][x] != color) {\n    return;\n  }\n\n  // Mark pixel as visited\n  pixels[y][x] = -1; // or some other sentinel value\n\n  // Recursively fill neighboring pixels\n  floodFill(x - 1, y, color, pixels, width, height);\n  floodFill(x + 1, y, color, pixels, width, height);\n  floodFill(x, y - 1, color, pixels, width, height);\n  floodFill(x, y + 1, color, pixels, width, height);\n}\n\nint countConnectedPixels(int** pixels, int width, int height) {\n  int count = 0;\n  for (int y = 0; y < height; y++) {\n    for (int x = 0; x < width; x++) {\n      if (pixels[y][x] == 1) { // or whatever color value you're interested in\n        floodFill(x, y, 1, pixels, width, height);\n        count++;\n      }\n    }\n  }\n  return count;\n}