// main.c\n#include <stdio.h>\n#include <dlfcn.h>\n\nvoid my_function() {\n    printf("Hello from main program\n");\n}\n\nint main() {\n    void* handle = dlopen("./mylib.so", RTLD_LAZY);\n    if (!handle) {\n        return 1;\n    }\n\n    void (*lib_func)() = dlsym(handle, "lib_function");\n    if (!lib_func) {\n        return 1;\n    }\n\n    lib_func();\n\n    dlclose(handle);\n    return 0;\n}