// Create a font that supports Unicode characters\nHFONT hFont = CreateFont(24, 0, 0, 0, FW_NORMAL, 0, 0, 0, DEFAULT_CHARSET, \n                         OUT_DEFAULT_PRECIS, CLIP_DEFAULT_PRECIS, \n                         DEFAULT_QUALITY, DEFAULT_PITCH | FF_DONTCARE, \n                         _T("Arial Unicode MS"));\n\n// Set the font for the device context\nSelectObject(hdc, hFont);\n\n// Render the Unicode text\nTCHAR szText[] = _T("\u4F60\u597D"); // Hello in Chinese\nTextOut(hdc, 10, 10, szText, _tcslen(szText));