// Sample code from the tutorial\n#include <xmlsecurity/xmlsec.h>\n\n// Load the XML document\nxmlDocPtr doc = xmlParseFile("example.xml");\n\n// Create a signature context\nxmlSecSignatureCtxPtr signatureCtx = xmlSecSignatureCtxCreate(NULL, NULL);\n\n// Set the signing key\nxmlSecKeyPtr key = xmlSecKeyReadFile("path/to/key");\nxmlSecSignatureCtxSetKey(signatureCtx, key);\n\n// Sign the document\nxmlSecSignatureCtxSign(signatureCtx, doc);