// Create a file mapping object\nHANDLE hFileMapping = CreateFileMapping(\n    INVALID_HANDLE_VALUE,  // Use a paging file\n    NULL,                  // Default security attributes\n    PAGE_READWRITE,        // Allow read/write access\n    0,                     // Size of the file mapping object (high 32 bits)\n    1024,                  // Size of the file mapping object (low 32 bits)\n    NULL                   // Name of the file mapping object\n);\n\n// Map the file to a region of memory\nLPVOID pSharedMemory = MapViewOfFile(\n    hFileMapping,          // File mapping object\n    FILE_MAP_WRITE,        // Desired access\n    0,                     // File offset (high 32 bits)\n    0,                     // File offset (low 32 bits)\n    1024                   // Number of bytes to map\n);