import java.lang.String;

// Note: GWT is a library used for web development, 
// and AsyncCallback is an interface from GWT library. 
// To make this code compile, you need to have GWT library in your classpath.

// Assuming GWT library is in your classpath
import com.google.gwt.core.client.GWT;
import com.google.gwt.user.client.rpc.AsyncCallback;

// GreetingService and GreetingServiceAsync should be generated by GWT compiler
// from your GreetingService interface
public class GreetingServiceAsyncImplorGreetingServiceClient {
    GreetingServiceAsync greetingService = GWT.create(GreetingService.class);

    public GreetingServiceAsyncImplorGreetingServiceClient() {
        greetingService.greetServer("Bob", new AsyncCallback<String>() {
            @Override
            public void onFailure(Throwable caught) {
                System.out.println("Error: " + caught.getMessage());
            }

            @Override
            public void onSuccess(String result) {
                System.out.println("Result: " + result);
            }
        });
    }

    public static void main(String[] args) {
        new GreetingServiceAsyncImplorGreetingServiceClient();
    }
}