import org.jooq.DSL;
import org.jooq.Record;
import org.jooq.Result;
import org.jooq.SQLDialect;
import org.jooq.impl.DSL;
import org.jooq.impl.DSLContext;
import org.jooq.impl.DefaultConfiguration;

// Assuming MY_TABLE and MY_COLUMN are custom classes generated by jOOQ
import static your.generated.package.MY_TABLE;
import static your.generated.package.MY_COLUMN;

public class QueryBuilder {
    public static void buildQuery() {
        DSLContext context = DSL.using(new DefaultConfiguration().set(SQLDialect.DEFAULT));
        Result<Record> result = context.selectFrom(MY_TABLE).where(MY_COLUMN.eq("SomeValue")).orderBy(MY_COLUMN.desc()).fetch();
    }

    public static void main(String[] args) {
        buildQuery();
    }
}