$c = new Criteria();\n\nif ($request->getPostParameter('price_from') > 0) {\n    $criterionFrom = $c->getNewCriterion(\n        HomeModelPeer::PRICE,\n        $request->getPostParameter('price_from'),\n        Criteria::GREATER_EQUAL\n    );\n    $c->addAnd($criterionFrom);\n}\n\nif ($request->getPostParameter('price_to') > 0) {\n    $criterionTo = $c->getNewCriterion(\n        HomeModelPeer::PRICE,\n        $request->getPostParameter('price_to'),\n        Criteria::LESS_EQUAL\n    );\n    $c->addAnd($criterionTo);\n}