<?php\nuse Ratchet\MessageComponentInterface;\nuse Ratchet\ConnectionInterface;\n\nclass SignalingServer implements MessageComponentInterface {\n    protected $peers = [];\n\n    public function onOpen(ConnectionInterface $conn) {\n        $this->peers[$conn->resourceId] = $conn;\n    }\n\n    public function onMessage(ConnectionInterface $from, $msg) {\n        $data = json_decode($msg, true);\n        if ($data['type'] === 'offer') {\n            // Relay the offer to the other peer\n            $this->peers[$data['target']]->send(json_encode(['type' => 'offer', 'data' => $data['data']]));\n        } elseif ($data['type'] === 'answer') {\n            // Relay the answer to the other peer\n            $this->peers[$data['target']]->send(json_encode(['type' => 'answer', 'data' => $data['data']]));\n        }\n    }\n\n    public function onClose(ConnectionInterface $conn) {\n        unset($this->peers[$conn->resourceId]);\n    }\n\n    public function onError(ConnectionInterface $conn, \Exception $e) {\n        // Handle errors\n    }\n}