function Validate(){\n    if(!empty($_POST["From"])&&!empty($_POST["Body"])){\n        return true;\n    }else{\n        return false;\n    }\n}\n\nfunction Postback(){\n    if(isset($_POST["Return"])){\n        return true;\n    }else{\n        return false;\n    }\n}\n\nfunction Email(){\n    static $Valid = null; // Initialize the static variable\n\n    if($Valid !== null){\n        return $Valid === 1;\n    }else{\n        if(Postback()){\n            if(Validate()){\n                $Valid = 1;\n                // Send mail here\n            }else{\n                $Valid = 0;\n            }\n            return $Valid === 1;\n        }\n    }\n}\n\nEmail();