use TYPO3\CMS\Frontend\ContentObject\ContentObjectRenderer;\n\npublic function renderTextElement($ttContentUid)\n{\n    $contentObject = \TYPO3\CMS\Core\Utility\GeneralUtility::makeInstance(ContentObjectRenderer::class);\n    $contentObject->start($ttContentUid, 'tt_content');\n    $renderedText = $contentObject->cObjGetSingle('TEXT', ['value' => '']);\n    return $renderedText;\n}