$mail = new PHPMailer();\n\n// Define the path to your HTML template\n$templatePath = 'template/index.htm';\n\n// Define the base directory for your images\n$baseDirectory = dirname(__FILE__);\n\n// Read the HTML template\n$body = file_get_contents($templatePath);\n\n// Make sure the image paths are absolute\n$body = preg_replace('/src="([^"]+)"/', 'src="' . $baseDirectory . '/$1"', $body);\n\n$mail->Subject = "PHPMailer Test Subject via mail(), basic";\n$mail->AltBody = "To view this message, please use an HTML compatible email viewer!";\n$mail->SetFrom('xxxxxxx', 'xxxxxxxxxx');\n$address = ".......@gmail.com";\n$mail->AddAddress($address, "xxxxxxx");\n\n// Pass the modified HTML to MsgHTML\n$mail->MsgHTML($body);\n\nif(!$mail->Send()) {\n    echo "Mailer Error: " . $mail->ErrorInfo;\n} else {\n    echo "Message sent!";\n}