$inputData = [\n    'name' => 'John Doe',\n    'email' => 'johndoe@example.com',\n    'phone' => '123-456-7890',\n];\n\n$validatedData = [];\n\nforeach ($inputData as $field => $value) {\n    switch ($field) {\n        case 'name':\n            $validatedData[$field] = filter_var($value, FILTER_SANITIZE_STRING);\n            break;\n        case 'email':\n            $validatedData[$field] = filter_var($value, FILTER_VALIDATE_EMAIL);\n            break;\n        case 'phone':\n            $validatedData[$field] = filter_var($value, FILTER_SANITIZE_NUMBER_INT);\n            break;\n        default:\n            // Handle unknown fields\n            break;\n    }\n}\n\n// Use $validatedData in your applicatio