// Set encryption key (keep it secret!)\n$encryption_key = 'your_secret_key_here';\n\n// Function to encrypt data\nfunction encrypt($data) {\n  global $encryption_key;\n  $encrypted_data = mcrypt_encrypt(MCRYPT_RIJNDAEL_256, $encryption_key, $data, MCRYPT_MODE_ECB);\n  return base64_encode($encrypted_data);\n}\n\n// Function to decrypt data\nfunction decrypt($encrypted_data) {\n  global $encryption_key;\n  $encrypted_data = base64_decode($encrypted_data);\n  return mcrypt_decrypt(MCRYPT_RIJNDAEL_256, $encryption_key, $encrypted_data, MCRYPT_MODE_ECB);\n}\n\n// Example usage:\n$data_to_encrypt = 'Hello, World!';\n$encrypted_data = encrypt($data_to_encrypt);\n\n// Set cookie with encrypted data\nsetcookie('encrypted_data', $encrypted_data);\n\n// Later, to decrypt the data:\n$encrypted_data = $_COOKIE['encrypted_data'];\n$decrypted_data = decrypt($encrypted_data);\n\necho $decrypted_data; // Outputs: Hello, World!