// Get the current URL\n$currentUrl = $_SERVER['REQUEST_URI'];\n\n// Extract the query string\n$queryString = parse_url($currentUrl, PHP_URL_QUERY);\n\n// Parse the query string into an array\nparse_str($queryString, $queryParams);\n\n// Check if the question_id is set in the query parameters\nif (isset($queryParams['question_id']) && !empty($queryParams['question_id'])) {\n    $questionId = $queryParams['question_id'];\n    // Use prepared statements to prevent SQL injection\n    $result = pg_execute($dbconn, "query_fetch", array($questionId));\n}