$drives = array();\n$letters = range('A', 'Z');\nforeach ($letters as $letter) {\n    $drive = $letter . ':';\n    if (is_dir($drive)) {\n        $drives[] = $drive;\n    }\n}\n\n// Add network drives\n$networkDrives = array('//server/share'); // Add your network drives here\n$drives = array_merge($drives, $networkDrives);\n\nforeach ($drives as $drive) {\n    echo $drive . "\n";\n    $dir = opendir($drive);\n    while (($file = readdir($dir)) !== false) {\n        echo $file . "\n";\n    }\n    closedir($dir);\n}