// Generate a public-private key pair using OpenSSL\n$privateKey = openssl_pkey_new(array(\n    'private_key_bits' => 2048,\n    'private_key_type' => OPENSSL_KEYTYPE_RSA,\n));\n\n// Get the private key in PEM format\nopenssl_pkey_export($privateKey, $privateKeyPem);\n\n// Get the public key in PEM format\n$publicKey = openssl_pkey_get_public($privateKey);\n$publicKeyPem = openssl_pkey_get_public($publicKey);\n\n// Encrypt data using the public key\n$data = 'Sensitive data';\nopenssl_public_encrypt($data, $encryptedData, $publicKeyPem);\n\n// Decrypt data using the private key\nopenssl_private_decrypt($encryptedData, $decryptedData, $privateKeyPem);\n\n// Store the public key in your script, and keep the private key secure\n$publicKeyPem = '...'; // hardcoded public key\n\n// Use the public key to encrypt data\nopenssl_public_encrypt($data, $encryptedData, $publicKeyPem);