try {\n    $pdo = new PDO('mysql:host=your_host;dbname=your_database', 'your_username', 'your_password');\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Assuming $docuTitle is your document title and $catID is the category ID\n    $stmt = $pdo->prepare('INSERT INTO documents (title, category_id) VALUES (:title, :category_id)');\n    $stmt->execute([':title' => $docuTitle, ':category_id' => $catID]);\n\n    echo 'Document title inserted successfully!';\n} catch (PDOException $e) {\n    echo 'Error: ' . $e->getMessage();\n}