function unzip($file, $to) {\n  $zip = fopen($file, 'rb');\n  $c = 0;\n  $fp = @fopen($to.'/'.substr($file, 0, -4), 'wb');\n  while (!feof($zip)) {\n    $header = fread($zip, 30);\n    if ($header == '') break;\n    $name = gzinflate(fread($zip, gzcompress(fread($zip, 14))));\n    $size = unpack('V', fread($zip, 4))[1];\n    $data = gzinflate(fread($zip, gzcompress(fread($zip, $size))));\n    fwrite($STDERR, "$name\n");\n    $fp = @fopen($to.'/'.$name, 'wb');\n    fwrite($fp, $data);\n    fclose($fp);\n  }\n  fclose($zip);\n}