use Zend\Mail\Message;\nuse Zend\Mail\Transport\Sendmail;\nuse Zend\Mime\Message as MimeMessage;\nuse Zend\Mime\Part as MimePart;\n\n// Create a mail message\n$message = new Message();\n$message->setBody('This is the text body');\n\n// Create a mime message for the HTML part\n$mimeMessage = new MimeMessage();\n$htmlPart = new MimePart('<html><body><img src="cid:image1"></body></html>');\n$htmlPart->type = 'text/html';\n$mimeMessage->addPart($htmlPart);\n\n// Create a mime part for the image\n$imagePart = new MimePart(file_get_contents('path/to/image.jpg'));\n$imagePart->type = 'image/jpeg';\n$imagePart->disposition = 'inline';\n$imagePart->filename = 'image1';\n$imagePart->id = 'image1';\n\n// Add the image part to the mime message\n$mimeMessage->addPart($imagePart);\n\n// Set the mime message as the mail message body\n$message->setBody($mimeMessage);\n\n// Send the mail\n$transport = new Sendmail();\n$transport->send($message);