<?php\n// Your database query and logic here...\n$email = $_POST['email'];\n$result = mysql_query("SELECT * FROM table WHERE email = '$email'");\n\nif ($result) {\n    $response = array('success' => true, 'message' => 'Email found!');\n} else {\n    $response = array('success' => false, 'message' => 'Email not found!');\n}\n\n// Set the correct HTTP headers to prevent XSS\nheader('Content-Type: application/json');\nheader('X-Content-Type-Options: nosniff');\n\n// Output the JSON response\necho json_encode($response);\nexit;