// Assuming you have a secure session ID and data\n$sessionId = bin2hex(random_bytes(32));\n$data = json_encode(['key' => 'value']);\n\n// Create a MySQL connection\n$conn = new mysqli('localhost', 'username', 'password', 'database');\n\n// Insert or update session data\n$stmt = $conn->prepare('INSERT INTO sessions (id, data) VALUES (?, ?) ON DUPLICATE KEY UPDATE data = VALUES(data)');\n$stmt->bind_param('ss', $sessionId, $data);\n$stmt->execute();