function getLastId($tableName, $columnName, $dbConnection) {\n    try {\n        $stmt = $dbConnection->prepare("SELECT MAX($columnName) AS lastId FROM $tableName");\n        $stmt->execute();\n        $result = $stmt->fetch();\n\n        if ($result !== false && $result['lastId'] !== null) {\n            return $result['lastId'];\n        } else {\n            // Handle the case when the table is empty or does not exist\n            return null;\n        }\n    } catch (PDOException $e) {\n        // Handle the exception, log it, and return a meaningful value\n        // For security reasons, do not expose the database error directly\n        return null;\n    }\n}\n\n// Example usage:\n$dsn = 'mysql:host=localhost;dbname=yourdatabase';\n$username = 'yourusername';\n$password = 'yourpassword';\n\ntry {\n    $pdo = new PDO($dsn, $username, $password);\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n} catch (PDOException $e) {\n    // Handle the connection exception\n    // Log it, and exit or return a meaningful value\n    exit();\n}\n\n$tableName = 'yourtable';\n$columnName = 'id';\n\n$lastId = getLastId($tableName, $columnName, $pdo);\nif ($lastId !== null) {\n    echo "The last ID is: $lastId";\n} else {\n    echo "Failed to retrieve the last ID.";\n}